mapper.select:
SELECT	*
FROM	TB_METTING
WHERE	meetingRoomName = #{meetingRoomName}
AND		start = #{start}
AND		end = #{end}

mapper.update:
UPDATE	TB_METTING
SET		user = #{user},
		cycleCount = #{cycleCount}
WHERE	meetingRoomName = #{meetingRoomName}
AND		start = #{start}
AND		end = #{end}

mapper.delete:
DELETE FROM TB_METTING
WHERE	meetingRoomName = #{meetingRoomName}
AND		start = #{start}
AND		end = #{end}
